var express = require('express')
const fs = require('fs')
const cors = require('cors')
const bodyParser = require('body-parser')
var nodemailer = require('nodemailer')
var app = express()
let port = process.env.PORT

app.use(
    cors({
        origin: '*',
    })
)

app.use(bodyParser.json())

app.post('/', (req, res) => {
    console.log('inside post')
    let currentOrder = req.body.curr

    var transporter = nodemailer.createTransport({
        service: 'gmail',
        auth: {
            user: 'splashordersmail@gmail.com',
            pass: process.env.pass,
        },
    })

    var mailOptions = {
        from: 'splashordersmail@gmail.com',
        to: currentOrder.order_address.email,
        subject: 'Your Order from Splash',
        html: 
        `
        <!DOCTYPE html>
        <html lang="en" style="padding: 40px; width: 100%;">
            <head>
                <meta charset="UTF-8">
                <meta http-equiv="X-UA-Compatible" content="IE=edge">
                <meta name="viewport" content="width=device-width, initial-scale=1.0">
                <title>Email</title>
            </head>
            <body style="background-color: #0d4d79 !important; padding: 20px; width: 95%; margin: auto;">
                <div style="display: flex; flex-direction: column; align-items: self-start; justify-content: space-evenly;">
                    <img src="data:image/jpeg;base64," style="align-self: center; width:50% ; max-width: 631px;"/>
                    <h3><strong>Your order from Splash will be delievered as soon as possible!</strong></h3>
                    <div style="display: flex; flex-direction: column; align-items: flex-start; justify-content: space-evenly; height: 100px;">
                        <p style="margin: 0%; font-weight: 600; font-size: medium;">Order ID: ${currentOrder.order_id}</p>
                        <p style="margin: 0%; font-weight: 600; font-size: medium;">Items: ${currentOrder.order_items}</p>
                        <p style="margin: 0%; font-weight: 600; font-size: medium;">Subtotal (exc. shipping): ${currentOrder.order_subtotal}</p>
                    </div>
                </div>

                <hr style="display: block;height: 1px;border: 0;border-top: 1px solid #000; margin: 1em 0; padding: 0;">
                
                <div style="margin: auto; display: flex; flex-direction: row; justify-content: space-evenly; align-items: center; width: 30%;">
                    <a href="https://splash-client.vercel.app/">
                        <img src="data:image/png;base64," style="width: 25px;"/>
                    </a>
                    <a href="https://www.facebook.com/profile.php?id=100086553070056">
                        <img src="data:image/svg+xml;base64," style="width: 30px;"/>
                    </a>
                    <a href="https://www.instagram.com/a.splash.furniture">
                        <img src="data:image/svg+xml;base64," style="width: 30px;"/>
                    </a>
                </div>

                <hr style="display: block;height: 1px;border: 0;border-top: 1px solid #000; margin: 1em 0; padding: 0;">

                <p style="margin: auto; margin-top: 50px; font-weight: 600; font-size: medium; width: 300px; text-align: center; color="black">
                    Tawfik Ahmed El-Bakry, Al Manteqah as Sadesah, Nasr City, Cairo Governorate 4450473
                </p>

                <footer style="margin-top: 50px; width: 100%; height:fit-content ; display: flex; justify-content: center; align-items: center;">
                    <p style="margin: 0%; font-weight: 600; font-size: medium;text-align: center;">
                        Splash © 2023 All Rights Reserved.
                    </p>
                </footer>
            </body>
        </html>
        `,
    }

    transporter.sendMail(mailOptions, function (error) {
        console.log('inside email 1')
        if (error) {
            console.log(error)
        }
    })

    let txt = currentOrder.order_address.apartment
        ? currentOrder.order_address.apartment
        : 'Not Specified'
    let txt2 = currentOrder.order_address.landmark
        ? currentOrder.order_address.landmark
        : 'Not Specified'
    let txt3 = currentOrder.order_address.instructions
        ? currentOrder.order_address.instructions
        : 'Not Specified'

    var mailOptions2 = {
        from: 'splashordersmail@gmail.com',
        to: 'splashordersmail@gmail.com',
        subject: `New Order Placed, ID: ${currentOrder.order_id}`,
        text: `Order ID: ${currentOrder.order_id}\n
        Subtotal (exc. shipping): ${currentOrder.order_subtotal}\n
        Time: ${currentOrder.order_datetime}\n
        Items: ${currentOrder.order_items_ids}\n
        ------------------------------------
        Client:\n
        Name: ${currentOrder.order_address.username}\n
        Phone Number: ${currentOrder.order_address.phone}\n
        Email: ${currentOrder.order_address.email}\n
        City: ${currentOrder.order_address.city}\n
        Area: ${currentOrder.order_address.area}\n
        Street: ${currentOrder.order_address.street}\n
        Building / Villa: ${currentOrder.order_address.building}\n
        Floor: ${currentOrder.order_address.floor}\n
        Apartment: ${txt}\n
        Landmark: ${txt2}\n
        Extra Instructions: ${txt3}\n`,
    }

    transporter.sendMail(mailOptions2, function (error) {
        console.log('inside email 2')
        if (error) {
            console.log(error)
        }
    })

    let jsonString = JSON.stringify(req.body.db, null, 4)
    fs.writeFile('./src/scripts/db.json', jsonString, (err) => {
        if (err) {
            console.log('Error writing file', err)
        } else {
            res.status(200).send('Done!')
        }
    })
})

app.listen(port, (err) => {
    if (err) throw err
    console.log(`server running on port ${port}`)
})
